import React, { useState, useCallback } from 'react';
import { motion } from 'framer-motion';
import LearningAssistant from './LearningAssistant';

const PROXY_URL = 'http://localhost:3001';
const MAX_RETRIES = 3;
const RETRY_DELAY = 500;

function Home() {
  const [url, setUrl] = useState('');
  const [videoId, setVideoId] = useState('');
  const [selectedLanguage, setSelectedLanguage] = useState('');
  const [availableLanguages, setAvailableLanguages] = useState([]);
  const [subtitles, setSubtitles] = useState([]);
  const [loading, setLoading] = useState(false);
  const [loadingStatus, setLoadingStatus] = useState('');
  const [error, setError] = useState('');

  const handleUrlChange = (e) => {
    const newUrl = e.target.value;
    setUrl(newUrl);
    setSubtitles([]);
    setError('');
  };

  const fetchWithRetry = async (url, options = {}, retries = MAX_RETRIES) => {
    for (let i = 0; i < retries; i++) {
      try {
        const response = await fetch(url, options);
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        return response;
      } catch (error) {
        if (i === retries - 1) throw error;
        await new Promise(resolve => setTimeout(resolve, RETRY_DELAY));
      }
    }
  };

  const getErrorMessage = (status) => {
    switch (status) {
      case 400:
        return 'Please enter a valid YouTube URL';
      case 404:
        return 'No captions available for this video. Please try another video.';
      case 500:
        return 'Unable to fetch subtitles. Please try again later.';
      default:
        return 'An error occurred. Please try again.';
    }
  };

  const extractVideoId = (url) => {
    const regExp = /^.*(youtu.be\/|v\/|u\/\w\/|embed\/|watch\?v=|&v=)([^#&?]*).*/;
    const match = url.match(regExp);
    return (match && match[2].length === 11) ? match[2] : null;
  };

  const fetchSubtitles = async () => {
    setLoading(true);
    setError('');
    setLoadingStatus('Extracting video ID...');

    const id = extractVideoId(url);
    if (!id) {
      setError('Please enter a valid YouTube URL');
      setLoading(false);
      return;
    }

    setVideoId(id);
    setLoadingStatus('Fetching available languages...');

    try {
      // Fetch available languages
      const tracksResponse = await fetchWithRetry(`${PROXY_URL}/api/subtitles/list?videoId=${id}`);
      const tracksData = await tracksResponse.json();
      
      if (!tracksData.tracks || tracksData.tracks.length === 0) {
        throw new Error('No captions available for this video');
      }

      setAvailableLanguages(tracksData.tracks);

      // If no language is selected, select the first available one
      if (!selectedLanguage && tracksData.tracks.length > 0) {
        setSelectedLanguage(tracksData.tracks[0].languageCode);
      }

      setLoadingStatus('Fetching subtitles...');

      // Fetch subtitles for the selected language
      const subtitlesResponse = await fetchWithRetry(
        `${PROXY_URL}/api/subtitles/fetch?videoId=${id}&languageCode=${selectedLanguage || tracksData.tracks[0].languageCode}`
      );
      const subtitlesData = await subtitlesResponse.json();
      setSubtitles(subtitlesData.subtitles);
    } catch (error) {
      console.error('Error:', error);
      setError(getErrorMessage(error.message.includes('404') ? 404 : 500));
    } finally {
      setLoading(false);
      setLoadingStatus('');
    }
  };

  const downloadSubtitles = () => {
    const text = subtitles.map(s => s.text).join('\n\n');
    const blob = new Blob([text], { type: 'text/plain' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `subtitles-${videoId}.txt`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      exit={{ opacity: 0, y: -20 }}
      className="container mx-auto px-4 py-8"
    >
      <div className="relative py-3 w-11/12 max-w-5xl mx-auto">
        <div className="relative px-4 py-10 bg-white shadow-lg sm:rounded-3xl sm:p-20">
          <div className="max-w-md mx-auto">
            <div className="divide-y divide-gray-200">
              <div className="py-8 text-base leading-6 space-y-4 text-gray-700 sm:text-lg sm:leading-7">
                <motion.h1
                  initial={{ opacity: 0, y: -20 }}
                  animate={{ opacity: 1, y: 0 }}
                  className="text-3xl font-bold text-center mb-8 text-gray-800"
                >
                  YouTube Subtitle Extractor
                </motion.h1>

                <motion.div
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ delay: 0.2 }}
                  className="space-y-4"
                >
                  <div>
                    <label htmlFor="url" className="block text-sm font-medium text-gray-700 mb-2">
                      YouTube URL
                    </label>
                    <input
                      type="text"
                      id="url"
                      value={url}
                      onChange={handleUrlChange}
                      placeholder="Enter YouTube video URL"
                      className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                    />
                  </div>

                  {availableLanguages.length > 0 && (
                    <div>
                      <label htmlFor="language" className="block text-sm font-medium text-gray-700 mb-2">
                        Select Language
                      </label>
                      <select
                        id="language"
                        value={selectedLanguage}
                        onChange={(e) => setSelectedLanguage(e.target.value)}
                        className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                      >
                        {availableLanguages.map((lang) => (
                          <option key={lang.languageCode} value={lang.languageCode}>
                            {lang.language} {lang.isAutoGenerated ? '(Auto-generated)' : ''}
                          </option>
                        ))}
                      </select>
                    </div>
                  )}

                  <motion.button
                    whileHover={{ scale: 1.05 }}
                    whileTap={{ scale: 0.95 }}
                    onClick={fetchSubtitles}
                    disabled={loading || !url}
                    className="w-full bg-blue-600 text-white px-6 py-3 rounded-lg font-bold hover:bg-blue-700 transition-colors duration-200 disabled:opacity-50 disabled:cursor-not-allowed"
                  >
                    {loading ? 'Loading...' : 'Extract Subtitles'}
                  </motion.button>
                </motion.div>

                {loadingStatus && (
                  <motion.div
                    initial={{ opacity: 0 }}
                    animate={{ opacity: 1 }}
                    className="text-center text-gray-600 mt-4"
                  >
                    {loadingStatus}
                  </motion.div>
                )}

                {error && (
                  <motion.div
                    initial={{ opacity: 0, y: -10 }}
                    animate={{ opacity: 1, y: 0 }}
                    className="mt-4 p-4 bg-red-100 text-red-700 rounded-lg"
                  >
                    {error}
                  </motion.div>
                )}

                {subtitles && subtitles.length > 0 && (
                  <motion.div
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    className="mt-8 bg-white rounded-lg shadow-lg p-6"
                  >
                    <div className="flex justify-between items-center mb-4">
                      <h2 className="text-2xl font-bold text-gray-800">Extracted Subtitles</h2>
                      <motion.button
                        whileHover={{ scale: 1.05 }}
                        whileTap={{ scale: 0.95 }}
                        onClick={downloadSubtitles}
                        className="bg-green-600 text-white px-4 py-2 rounded-lg font-bold hover:bg-green-700 transition-colors duration-200"
                      >
                        Download Subtitles
                      </motion.button>
                    </div>
                    <div className="bg-gray-50 rounded-lg p-4 max-h-96 overflow-y-auto">
                      {subtitles.map((subtitle, index) => (
                        <motion.div
                          key={index}
                          initial={{ opacity: 0, x: -20 }}
                          animate={{ opacity: 1, x: 0 }}
                          transition={{ delay: index * 0.1 }}
                          className="mb-2 text-gray-700"
                        >
                          {subtitle.text}
                        </motion.div>
                      ))}
                    </div>
                    <LearningAssistant subtitles={subtitles.map(s => s.text).join('\n')} />
                  </motion.div>
                )}
              </div>
            </div>
          </div>
        </div>
      </div>
    </motion.div>
  );
}

export default Home;
