import React, { useState } from 'react';

const LANGUAGES = [
  { code: 'en', name: 'English' },
  { code: 'hi', name: 'Hindi' },
  { code: 'gu', name: 'Gujarati' },
  { code: 'es', name: 'Spanish' },
  { code: 'fr', name: 'French' },
  { code: 'de', name: 'German' },
  { code: 'ja', name: 'Japanese' },
  { code: 'zh', name: 'Chinese' },
  { code: 'ar', name: 'Arabic' },
  { code: 'bn', name: 'Bengali' },
];

// Proxy server URL
const PROXY_URL = 'http://localhost:3001';

function App() {
  const [url, setUrl] = useState('');
  const [language, setLanguage] = useState('en');
  const [subtitles, setSubtitles] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const extractVideoId = (url) => {
    const regExp = /^.*(youtu.be\/|v\/|u\/\w\/|embed\/|watch\?v=|&v=)([^#&?]*).*/;
    const match = url.match(regExp);
    return (match && match[2].length === 11) ? match[2] : null;
  };

  const checkVideoExists = async (videoId) => {
    try {
      const response = await fetch(`https://www.youtube.com/oembed?url=https://www.youtube.com/watch?v=${videoId}&format=json`);
      return response.ok;
    } catch (err) {
      console.error('Error checking video:', err);
      return false;
    }
  };

  const formatTime = (seconds) => {
    const date = new Date(seconds * 1000);
    return date.toISOString().substr(11, 8);
  };

  const fetchSubtitles = async () => {
    setError('');
    setSubtitles('');
    setLoading(true);

    const videoId = extractVideoId(url);
    if (!videoId) {
      setError('Invalid YouTube URL');
      setLoading(false);
      return;
    }

    console.log('Video ID:', videoId);
    console.log('Selected language:', language);

    // First check if the video exists
    const videoExists = await checkVideoExists(videoId);
    if (!videoExists) {
      setError('Video not found or is private');
      setLoading(false);
      return;
    }

    try {
      // First, get the list of available caption tracks
      const listUrl = `${PROXY_URL}/api/subtitles/list?videoId=${videoId}`;
      console.log('Fetching caption tracks:', listUrl);

      const listResponse = await fetch(listUrl);
      const listData = await listResponse.json();
      
      if (!listResponse.ok) {
        throw new Error(listData.error || 'Failed to fetch caption tracks');
      }

      console.log('Available tracks:', listData);

      // Find the track for the selected language
      const selectedTrack = listData.find(track => track.languageCode === language);
      if (!selectedTrack) {
        setError('No subtitles available for this language');
        setLoading(false);
        return;
      }

      // Fetch the subtitles using the selected track
      const subtitleUrl = `${PROXY_URL}/api/subtitles/fetch?videoId=${videoId}&trackId=${selectedTrack.id}`;
      console.log('Fetching subtitles:', subtitleUrl);

      const response = await fetch(subtitleUrl);
      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.error || 'Failed to fetch subtitles');
      }

      console.log('Subtitles response:', data);

      if (!data.subtitles || data.subtitles.length === 0) {
        setError('No subtitle content found');
        setLoading(false);
        return;
      }

      // Format subtitles with timestamps
      const formattedSubtitles = data.subtitles
        .map(sub => `[${formatTime(sub.start)}] ${sub.text}`)
        .join('\n');

      setSubtitles(formattedSubtitles);
    } catch (err) {
      console.error('Error fetching subtitles:', err);
      setError(err.message || 'Error fetching subtitles. Please try again.');
    }

    setLoading(false);
  };

  const downloadSubtitles = () => {
    const element = document.createElement('a');
    const file = new Blob([subtitles], { type: 'text/plain' });
    element.href = URL.createObjectURL(file);
    element.download = 'subtitles.txt';
    document.body.appendChild(element);
    element.click();
    document.body.removeChild(element);
  };

  return (
    <div className="min-h-screen bg-gray-100 py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-2xl mx-auto">
        <div className="bg-white rounded-lg shadow-lg p-6 space-y-6">
          <h1 className="text-2xl font-bold text-center text-gray-800">
            YouTube Subtitle Extractor
          </h1>
          
          <div className="space-y-4">
            <input
              type="text"
              value={url}
              onChange={(e) => setUrl(e.target.value)}
              placeholder="Paste YouTube URL here"
              className="w-full px-4 py-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
            />
            
            <select
              value={language}
              onChange={(e) => setLanguage(e.target.value)}
              className="w-full px-4 py-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
            >
              {LANGUAGES.map((lang) => (
                <option key={lang.code} value={lang.code}>
                  {lang.name}
                </option>
              ))}
            </select>

            <button
              onClick={fetchSubtitles}
              disabled={loading}
              className="w-full bg-blue-500 text-white px-4 py-2 rounded-md hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
            >
              {loading ? 'Loading...' : 'ðŸš€ Extract Text'}
            </button>
          </div>

          {error && (
            <div className="text-red-500 text-center p-4 bg-red-50 rounded-md">
              {error}
            </div>
          )}

          {subtitles && (
            <div className="space-y-4">
              <div className="max-h-60 overflow-y-auto p-4 bg-gray-50 rounded-md">
                <p className="text-gray-700 whitespace-pre-wrap">{subtitles}</p>
              </div>
              
              <button
                onClick={downloadSubtitles}
                className="w-full bg-green-500 text-white px-4 py-2 rounded-md hover:bg-green-600 focus:outline-none focus:ring-2 focus:ring-green-500 focus:ring-offset-2 transition-colors"
              >
                ðŸ“¥ Download Subtitles as .txt
              </button>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
