import React, { useState } from 'react';
import { motion } from 'framer-motion';

const PROXY_URL = 'http://localhost:3001';

function LearningAssistant({ subtitles }) {
  const [loading, setLoading] = useState(false);
  const [learningContent, setLearningContent] = useState('');
  const [error, setError] = useState('');

  const handleLearn = async () => {
    setLoading(true);
    setError('');
    setLearningContent('');

    try {
      const response = await fetch(`${PROXY_URL}/api/learn`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ subtitles }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Failed to generate learning content');
      }

      const data = await response.json();
      setLearningContent(data.content);
    } catch (error) {
      console.error('Error:', error);
      setError(error.message || 'Failed to generate learning content');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="mt-8">
      <motion.button
        whileHover={{ scale: 1.02 }}
        whileTap={{ scale: 0.98 }}
        onClick={handleLearn}
        disabled={loading}
        className={`w-full py-2 px-4 rounded-lg text-white font-bold ${
          loading ? 'bg-gray-400 cursor-not-allowed' : 'bg-purple-500 hover:bg-purple-600'
        }`}
      >
        {loading ? (
          <div className="flex items-center justify-center space-x-2">
            <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white"></div>
            <span>Generating Learning Guide...</span>
          </div>
        ) : (
          'Learn This Topic'
        )}
      </motion.button>

      {error && (
        <motion.div
          initial={{ opacity: 0, y: 10 }}
          animate={{ opacity: 1, y: 0 }}
          className="mt-4 p-4 bg-red-100 text-red-700 rounded-lg font-bold"
        >
          {error}
        </motion.div>
      )}

      {learningContent && (
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5 }}
          className="mt-6 bg-white rounded-lg shadow-lg p-6"
        >
          <h3 className="text-xl font-bold text-gray-800 mb-4">Learning Guide</h3>
          <div className="prose max-w-none">
            {learningContent.split('\n').map((line, index) => (
              <motion.p
                key={index}
                initial={{ opacity: 0, x: -20 }}
                animate={{ opacity: 1, x: 0 }}
                transition={{ delay: index * 0.1 }}
                className="mb-2"
              >
                {line}
              </motion.p>
            ))}
          </div>
        </motion.div>
      )}
    </div>
  );
}

export default LearningAssistant;
