import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';

function LearningAssistant({ subtitles }) {
  const [isLoading, setIsLoading] = useState(false);
  const [learningContent, setLearningContent] = useState(null);
  const [error, setError] = useState(null);

  const handleLearn = async () => {
    setIsLoading(true);
    setError(null);

    try {
      const response = await fetch('http://localhost:3001/api/learn', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          subtitles: subtitles,
          prompt: "I am a student and I want to learn this topic in a simple and structured way. Please break down the topic into easy-to-understand sections. Explain each section in simple English with practical examples. Also, include any visuals, analogies, or real-life scenarios if helpful. Make it beginner-friendly and interactive like a tutor would."
        }),
      });

      if (!response.ok) {
        throw new Error('Failed to get learning content');
      }

      const data = await response.json();
      setLearningContent(data.content);
    } catch (err) {
      setError('Failed to generate learning content. Please try again.');
      console.error('Error:', err);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="mt-6">
      <motion.button
        whileHover={{ scale: 1.05 }}
        whileTap={{ scale: 0.95 }}
        onClick={handleLearn}
        disabled={isLoading}
        className="bg-purple-600 text-white px-6 py-3 rounded-lg font-bold hover:bg-purple-700 transition-colors duration-200 disabled:opacity-50 disabled:cursor-not-allowed"
      >
        {isLoading ? 'Generating Learning Content...' : 'Learn This Topic'}
      </motion.button>

      <AnimatePresence>
        {error && (
          <motion.div
            initial={{ opacity: 0, y: -10 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -10 }}
            className="mt-4 p-4 bg-red-100 text-red-700 rounded-lg"
          >
            {error}
          </motion.div>
        )}

        {learningContent && (
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: 20 }}
            className="mt-6 p-6 bg-white rounded-lg shadow-lg"
          >
            <h3 className="text-xl font-bold text-gray-800 mb-4">Learning Guide</h3>
            <div className="prose max-w-none">
              {learningContent.split('\n').map((paragraph, index) => (
                <p key={index} className="mb-4 text-gray-700">
                  {paragraph}
                </p>
              ))}
            </div>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
}

export default LearningAssistant;
