import React, { useState } from 'react';

const PROXY_URL = 'http://localhost:3001';
const MAX_RETRIES = 3;
const RETRY_DELAY = 1000; // 1 second

const languages = [
  { code: 'en', name: 'English' },
  { code: 'es', name: 'Spanish' },
  { code: 'fr', name: 'French' },
  { code: 'de', name: 'German' },
  { code: 'it', name: 'Italian' },
  { code: 'pt', name: 'Portuguese' },
  { code: 'ru', name: 'Russian' },
  { code: 'ja', name: 'Japanese' },
  { code: 'ko', name: 'Korean' },
  { code: 'zh', name: 'Chinese' }
];

function App() {
  const [url, setUrl] = useState('');
  const [language, setLanguage] = useState('en');
  const [subtitles, setSubtitles] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const extractVideoId = (url) => {
    const regExp = /^.*(youtu.be\/|v\/|u\/\w\/|embed\/|watch\?v=|&v=)([^#&?]*).*/;
    const match = url.match(regExp);
    return (match && match[2].length === 11) ? match[2] : null;
  };

  const formatTime = (seconds) => {
    const date = new Date(seconds * 1000);
    const hh = date.getUTCHours();
    const mm = date.getUTCMinutes();
    const ss = date.getUTCSeconds().toString().padStart(2, '0');
    const ms = date.getUTCMilliseconds().toString().padStart(3, '0');
    return `${hh > 0 ? hh + ':' : ''}${mm.toString().padStart(2, '0')}:${ss}.${ms}`;
  };

  const sleep = (ms) => new Promise(resolve => setTimeout(resolve, ms));

  const fetchWithRetry = async (url, options = {}, retries = MAX_RETRIES) => {
    try {
      const response = await fetch(url, options);
      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || `HTTP error! status: ${response.status}`);
      }
      return response;
    } catch (error) {
      if (retries > 0) {
        console.log(`Retrying... ${retries} attempts left`);
        await sleep(RETRY_DELAY);
        return fetchWithRetry(url, options, retries - 1);
      }
      throw error;
    }
  };

  const fetchSubtitles = async () => {
    setError('');
    setSubtitles('');
    setLoading(true);

    try {
      const videoId = extractVideoId(url);
      if (!videoId) {
        throw new Error('Invalid YouTube URL');
      }

      console.log('Video ID:', videoId);
      console.log('Selected language:', language);

      // First, get the list of available caption tracks
      console.log('Fetching caption tracks:', `${PROXY_URL}/api/subtitles/list?videoId=${videoId}`);
      const tracksResponse = await fetchWithRetry(
        `${PROXY_URL}/api/subtitles/list?videoId=${videoId}`
      );
      
      const tracks = await tracksResponse.json();
      console.log('Available tracks:', tracks);

      if (!tracks || tracks.length === 0) {
        throw new Error('No captions available for this video');
      }

      // Find the track for the selected language
      const track = tracks.find(t => t.languageCode === language);
      if (!track) {
        throw new Error(`No captions available in ${languages.find(l => l.code === language)?.name || language}`);
      }

      // Fetch the actual subtitles
      console.log('Fetching subtitles for track:', track);
      const subtitlesResponse = await fetchWithRetry(
        `${PROXY_URL}/api/subtitles/fetch?videoId=${videoId}&trackId=${track.id}`
      );

      const data = await subtitlesResponse.json();
      console.log('Received subtitles data:', data);

      if (!data.subtitles || data.subtitles.length === 0) {
        throw new Error('No subtitle content found');
      }

      // Format subtitles with timestamps
      const formattedSubtitles = data.subtitles
        .map(sub => `[${formatTime(sub.start)}] ${sub.text}`)
        .join('\n');

      setSubtitles(formattedSubtitles);
    } catch (error) {
      console.error('Error fetching subtitles:', error);
      if (error.message.includes('Failed to fetch')) {
        setError('Unable to connect to the server. Please make sure the server is running.');
      } else {
        setError(error.message);
      }
    } finally {
      setLoading(false);
    }
  };

  const downloadSubtitles = () => {
    if (!subtitles) return;

    const blob = new Blob([subtitles], { type: 'text/plain' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'subtitles.txt';
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  return (
    <div className="min-h-screen bg-gray-100 py-6 flex flex-col justify-center sm:py-12">
      <div className="relative py-3 sm:max-w-xl sm:mx-auto">
        <div className="relative px-4 py-10 bg-white shadow-lg sm:rounded-3xl sm:p-20">
          <div className="max-w-md mx-auto">
            <div className="divide-y divide-gray-200">
              <div className="py-8 text-base leading-6 space-y-4 text-gray-700 sm:text-lg sm:leading-7">
                <h1 className="text-3xl font-bold text-center mb-8 text-gray-900">YouTube Subtitle Extractor</h1>
                
                <div className="mb-4">
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    YouTube URL
                  </label>
                  <input
                    type="text"
                    value={url}
                    onChange={(e) => setUrl(e.target.value)}
                    placeholder="https://www.youtube.com/watch?v=..."
                    className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500"
                  />
                </div>

                <div className="mb-4">
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Language
                  </label>
                  <select
                    value={language}
                    onChange={(e) => setLanguage(e.target.value)}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500"
                  >
                    {languages.map((lang) => (
                      <option key={lang.code} value={lang.code}>
                        {lang.name}
                      </option>
                    ))}
                  </select>
                </div>

                <div className="flex space-x-4">
                  <button
                    onClick={fetchSubtitles}
                    disabled={loading || !url}
                    className={`flex-1 px-4 py-2 rounded-md text-white font-medium ${
                      loading || !url
                        ? 'bg-gray-400 cursor-not-allowed'
                        : 'bg-indigo-600 hover:bg-indigo-700'
                    }`}
                  >
                    {loading ? 'Extracting...' : 'Extract Subtitles'}
                  </button>

                  <button
                    onClick={downloadSubtitles}
                    disabled={!subtitles}
                    className={`flex-1 px-4 py-2 rounded-md text-white font-medium ${
                      !subtitles
                        ? 'bg-gray-400 cursor-not-allowed'
                        : 'bg-green-600 hover:bg-green-700'
                    }`}
                  >
                    Download
                  </button>
                </div>

                {error && (
                  <div className="mt-4 p-4 bg-red-50 border border-red-200 rounded-md">
                    <p className="text-sm text-red-600">{error}</p>
                  </div>
                )}

                {subtitles && (
                  <div className="mt-4">
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Subtitles
                    </label>
                    <div className="h-64 overflow-y-auto p-4 bg-gray-50 rounded-md border border-gray-200">
                      <pre className="text-sm whitespace-pre-wrap">{subtitles}</pre>
                    </div>
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
