import React, { useState, useCallback } from 'react';
import './App.css';

const PROXY_URL = 'http://localhost:3001';
const MAX_RETRIES = 3;
const RETRY_DELAY = 500;

const languages = [
  { code: 'en', name: 'English', available: true },
  { code: 'hi', name: 'Hindi', available: true },
  { code: 'es', name: 'Spanish', available: true },
  { code: 'fr', name: 'French', available: true },
  { code: 'de', name: 'German', available: true },
  { code: 'it', name: 'Italian', available: true },
  { code: 'pt', name: 'Portuguese', available: true },
  { code: 'ru', name: 'Russian', available: true },
  { code: 'ja', name: 'Japanese', available: true },
  { code: 'ko', name: 'Korean', available: true },
  { code: 'zh', name: 'Chinese', available: true }
];

function App() {
  const [url, setUrl] = useState('');
  const [videoId, setVideoId] = useState('');
  const [selectedLanguage, setSelectedLanguage] = useState('en');
  const [subtitles, setSubtitles] = useState([]);
  const [loading, setLoading] = useState(false);
  const [loadingStatus, setLoadingStatus] = useState('');
  const [error, setError] = useState('');
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  // Debounced URL input handler
  const handleUrlChange = useCallback((e) => {
    const newUrl = e.target.value;
    setUrl(newUrl);
    
    // Clear previous results when URL changes
    if (newUrl !== url) {
      setSubtitles([]);
      setError('');
    }
  }, [url]);

  const fetchWithRetry = async (url, options = {}, retries = MAX_RETRIES) => {
    try {
      const response = await fetch(url, options);
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      return await response.json();
    } catch (error) {
      if (retries > 0) {
        console.log(`Retrying... ${retries} attempts left`);
        await new Promise(resolve => setTimeout(resolve, RETRY_DELAY));
        return fetchWithRetry(url, options, retries - 1);
      }
      throw error;
    }
  };

  const extractVideoId = (url) => {
    const regExp = /^.*(youtu.be\/|v\/|u\/\w\/|embed\/|watch\?v=|&v=)([^#&?]*).*/;
    const match = url.match(regExp);
    return (match && match[2].length === 11) ? match[2] : null;
  };

  const fetchSubtitles = async () => {
    const startTime = performance.now();
    setLoading(true);
    setError('');
    setSubtitles([]);
    setLoadingStatus('Initializing...');

    try {
      const id = extractVideoId(url);
      if (!id) {
        throw new Error('Invalid YouTube URL');
      }
      setVideoId(id);
      setLoadingStatus('Validating video...');
      console.log('Video ID:', id);
      console.log('Selected language:', selectedLanguage);

      // First check if video exists and get available caption tracks
      setLoadingStatus('Fetching available caption tracks...');
      console.log('Fetching caption tracks:', `${PROXY_URL}/api/subtitles/list?videoId=${id}`);
      const tracksStartTime = performance.now();
      const tracks = await fetchWithRetry(`${PROXY_URL}/api/subtitles/list?videoId=${id}`);
      const tracksEndTime = performance.now();
      console.log(`Caption tracks fetch took: ${(tracksEndTime - tracksStartTime).toFixed(2)}ms`);
      console.log('Available tracks:', tracks);

      if (!tracks || tracks.length === 0) {
        throw new Error('No captions available for this video');
      }

      // Find the selected language track
      let track = tracks.find(t => t.languageCode === selectedLanguage);
      
      // If selected language is not available, use the first available track
      if (!track && tracks.length > 0) {
        track = tracks[0];
        setSelectedLanguage(track.languageCode);
        console.log(`Selected language not available. Using ${track.name} instead.`);
      }

      if (!track) {
        throw new Error('No captions available for this video');
      }

      setLoadingStatus(`Fetching ${track.name} subtitles...`);
      console.log('Fetching subtitles for track:', track);

      // Fetch the actual subtitles
      const subtitlesStartTime = performance.now();
      const response = await fetchWithRetry(
        `${PROXY_URL}/api/subtitles/fetch?videoId=${id}&trackId=${track.languageCode}`
      );
      const subtitlesEndTime = performance.now();
      console.log(`Subtitles fetch took: ${(subtitlesEndTime - subtitlesStartTime).toFixed(2)}ms`);
      console.log('Received subtitles data:', response);

      if (!response.subtitles || response.subtitles.length === 0) {
        throw new Error('No subtitle content found');
      }

      setLoadingStatus('Processing subtitles...');
      setSubtitles(response.subtitles);
      const endTime = performance.now();
      console.log(`Total extraction time: ${(endTime - startTime).toFixed(2)}ms`);
      setLoadingStatus('Complete!');
    } catch (error) {
      console.error('Error:', error);
      setError(error.message);
      setLoadingStatus('Error occurred');
    } finally {
      setLoading(false);
    }
  };

  const downloadSubtitles = () => {
    if (subtitles.length === 0) return;

    // Create text content from subtitles
    const textContent = subtitles.map(subtitle => subtitle.text).join('\n\n');

    // Create a blob with the text content
    const blob = new Blob([textContent], { type: 'text/plain;charset=utf-8' });

    // Create a download link
    const url = window.URL.createObjectURL(blob);
    const link = document.createElement('a');
    link.href = url;
    
    // Set the filename using video ID and language
    const filename = `subtitles_${videoId}_${selectedLanguage}.txt`;
    link.setAttribute('download', filename);
    
    // Trigger the download
    document.body.appendChild(link);
    link.click();
    
    // Cleanup
    document.body.removeChild(link);
    window.URL.revokeObjectURL(url);
  };

  return (
    <div className="min-h-screen bg-gray-100 ">
      {/* Navigation Bar */}
      <nav className="bg-white shadow-lg">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between h-16">
            <div className="flex items-center">
              <span className="text-xl font-bold text-blue-600">YouTube Subtitle Extractor</span>
            </div>
            
            {/* Desktop Navigation */}
            <div className="hidden md:flex items-center space-x-4">
              <a href="#" className="text-gray-700 hover:text-blue-600 px-3 py-2 rounded-md text-sm font-medium">Home</a>
              <a href="#" className="text-gray-700 hover:text-blue-600 px-3 py-2 rounded-md text-sm font-medium">About Us</a>
              <a href="#" className="text-gray-700 hover:text-blue-600 px-3 py-2 rounded-md text-sm font-medium">Contact Us</a>
            </div>

            {/* Mobile menu button */}
            <div className="md:hidden flex items-center">
              <button
                onClick={() => setIsMenuOpen(!isMenuOpen)}
                className="inline-flex items-center justify-center p-2 rounded-md text-gray-700 hover:text-blue-600 focus:outline-none"
              >
                <svg
                  className="h-6 w-6"
                  xmlns="http://www.w3.org/2000/svg"
                  fill="none"
                  viewBox="0 0 24 24"
                  stroke="currentColor"
                >
                  {isMenuOpen ? (
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                  ) : (
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" />
                  )}
                </svg>
              </button>
            </div>
          </div>
        </div>

        {/* Mobile Navigation Menu */}
        {isMenuOpen && (
          <div className="md:hidden">
            <div className="px-2 pt-2 pb-3 space-y-1 sm:px-3">
              <a href="#" className="block text-gray-700 hover:text-blue-600 px-3 py-2 rounded-md text-base font-medium">Home</a>
              <a href="#" className="block text-gray-700 hover:text-blue-600 px-3 py-2 rounded-md text-base font-medium">About Us</a>
              <a href="#" className="block text-gray-700 hover:text-blue-600 px-3 py-2 rounded-md text-base font-medium">Contact Us</a>
            </div>
          </div>
        )}
      </nav>

      {/* Main Content */}
      <div className="py-6 flex flex-col justify-center sm:py-12">
        <div className="relative py-3 w-11/12 max-w-5xl mx-auto">
          <div className="relative px-4 py-10 bg-white shadow-lg sm:rounded-3xl sm:p-20">
            <div className="w-full max-w-5xl mx-auto">
              <div className="divide-y divide-gray-200">
                <div className="py-8 text-base leading-6 space-y-4 text-gray-700 sm:text-lg sm:leading-7">
                  <h1 className="text-3xl font-bold text-center mb-8 text-blue-600">Extract Subtitles</h1>
                  
                  <div className="mb-4">
                    <input
                      type="text"
                      value={url}
                      onChange={handleUrlChange}
                      placeholder="Enter YouTube URL"
                      className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                    />
                  </div>

                  <div className="mb-4">
                    <select
                      value={selectedLanguage}
                      onChange={(e) => setSelectedLanguage(e.target.value)}
                      className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                    >
                      {languages.map(lang => (
                        <option 
                          key={lang.code} 
                          value={lang.code}
                          disabled={!lang.available}
                          className={!lang.available ? 'text-gray-400' : ''}
                        >
                          {lang.name}
                        </option>
                      ))}
                    </select>
                  </div>

                  <button
                    onClick={fetchSubtitles}
                    disabled={loading || !url}
                    className={`w-full py-2 px-4 rounded-lg text-white font-semibold ${
                      loading || !url
                        ? 'bg-gray-400 cursor-not-allowed'
                        : 'bg-blue-500 hover:bg-blue-600'
                    }`}
                  >
                    {loading ? 'Loading...' : 'Extract Subtitles'}
                  </button>

                  {loading && (
                    <div className="mt-4 p-4 bg-blue-50 rounded-lg">
                      <div className="flex items-center space-x-2">
                        <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-blue-500"></div>
                        <span className="text-blue-700">{loadingStatus}</span>
                      </div>
                    </div>
                  )}

                  {error && (
                    <div className="mt-4 p-4 bg-red-100 text-red-700 rounded-lg">
                      {error}
                    </div>
                  )}

                  {subtitles.length > 0 && (
                    <div className="mt-8">
                      <div className="flex justify-between items-center mb-4">
                        <h2 className="text-xl font-semibold">Subtitles:</h2>
                        <button
                          onClick={downloadSubtitles}
                          className="px-4 py-2 bg-green-500 text-white rounded-lg hover:bg-green-600 transition-colors duration-200 flex items-center space-x-2"
                        >
                          <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" viewBox="0 0 20 20" fill="currentColor">
                            <path fillRule="evenodd" d="M3 17a1 1 0 011-1h12a1 1 0 110 2H4a1 1 0 01-1-1zm3.293-7.707a1 1 0 011.414 0L9 10.586V3a1 1 0 112 0v7.586l1.293-1.293a1 1 0 111.414 1.414l-3 3a1 1 0 01-1.414 0l-3-3a1 1 0 010-1.414z" clipRule="evenodd" />
                          </svg>
                          <span>Download</span>
                        </button>
                      </div>
                      <div className="bg-gray-50 p-4 rounded-lg max-h-96 overflow-y-auto">
                        {subtitles.map((subtitle, index) => (
                          <p key={index} className="mb-2">
                            {subtitle.text}
                          </p>
                        ))}
                      </div>
                    </div>
                  )}
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
